import React from 'react';
import Card from "./card.jsx";
import Filters from "./filters";

class Gallery extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            apartments: this.props.apartments,
            filtered: this.props.apartments,
            cities: this.props.cities
        }
    };

    checkRange = ( value, min, max, label) => {
        let cur_val = value[label];
        if (label === 'price') {
            cur_val = value[label]*100000;
        }
        min = (min === undefined || min === "") ?  0 : min;
        max = (max === undefined || max === "") ?  Infinity : max;

        return (cur_val >= min && cur_val <= max);
    };

    data_price_multiplication = (price) => {
        if (price && typeof price !== "number") {
            let multiplication_price_max = price.slice(-1);

            if (multiplication_price_max === "k") {
                price = parseInt(price.slice(1)) * 1000;
            } else if (multiplication_price_max === "M") {
                price = parseInt(price.slice(1)) * 1000000;
            }
        }
        return price;
    };
    componentWillMount() {
        this.state.filtered.map(function (apartment) {apartment.activeApp = true
        });
    }

    updateGalleryItems  = (data) => {
        const {filtered, cities, apartments} = this.state;

        // if (data.city) {
        //     this.setState({
        //     filtered : filtered.map(function (apartment) {
        //         const city = cities.find(city => city.id === apartment.cityId);
        //         const city_name = city.label;
        //         apartment.active = city_name.toLowerCase().includes(data.city.toLowerCase());
        //         })
        //     });
        //     console.log(this.state.filtered);
        // }
        if (data.city) {
             filtered.map(function (apartment) {
                const city = cities.find(city => city.id === apartment.cityId);
                const city_name = city.label;

                if (city_name === "") {

                }

                if(city_name.toLowerCase().includes(data.city.toLowerCase())) {
                    apartment.activeApp = city_name.toLowerCase().includes(data.city.toLowerCase())
                } else {
                    apartment.activeApp = city_name === ""
                }
                // apartment.activeApp = city_name.toLowerCase().includes(data.city.toLowerCase()) || city_name === "";
            }, this.setState({filtered: this.state.filtered}));

            console.log(data.city)
        }

        if (data.price_min || data.price_max) {
            data.price_min = this.data_price_multiplication(data.price_min);
            data.price_max = this.data_price_multiplication(data.price_max);

            if (data.price_max || data.price_min) {
                this.setState({
                    filtered : filtered.filter(obj => this.checkRange(obj, data.price_min , data.price_max , "price"))
                });
            }
        }
        if (data.bed_min || data.bed_max) {
                this.setState({
                    filtered : filtered.filter (obj => this.checkRange(obj, data.bed_min, data.bed_max, "number_of_beds"))
                });
        }
        if (data.room_min || data.room_max) {
            this.setState({
                    filtered : filtered.filter(obj => this.checkRange(obj, data.room_min, data.room_max, "number_of_rooms"))
            });
        }
        if (data.no_filters){
            this.setState({
                filtered : apartments,
            });
        }
    };

    filtered_len_0 = () => {
        const {filtered, apartments } = this.state;
        if (filtered.length === 0){
            alert("There are no apartments that match your search \n (The apartments will appear without the filters)");
            this.setState({
                filtered : apartments,
            });
        }
    };


    render() {
        this.filtered_len_0();
        const { cities, filtered } = this.state;
        console.log(this.state.filtered);
        const buildSquareDetails = () => {
            return filtered.map((item, i) => {
                return item['activeApp'] ?
                    <Card {...item} cities={cities} type={this.props.type} key={i}/>
                    : null

            })
        };

        return (
            <div style={{overflow:"hidden"}}>
                <div>
                    <Filters updateGalleryItems={this.updateGalleryItems} filtered={this.state.filtered}/>
                </div>

                <div className={"row m-0 d-flex justify-content-around"}>
                    {buildSquareDetails()}
                </div>
            </div>
        )
    }
}


export default Gallery;
