import React from 'react';
import Card from "./card.jsx";
import Filters from "./filters";
import {apartments} from "../app_data/apartments_array";
import {cities} from "../app_data/cities_array";

class Gallery extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            apartments: apartments,
        }
    }

    updateGalleryItems = (data) => {

        let array = apartments;

         const checkRange = ( min, max, value, label) => {

            min = (min === undefined || min === "") ?  0 : min;
            max = (max === undefined || max === "") ?  Infinity : max;

            return (value[label] >= min && value[label] <= max);
        };


        if (data.city) {
            array = apartments.filter(function (apartment) {
                const city = cities.find(city => city.id === apartment.cityId);
                const city_name = city.label;
                if (city_name.includes(data.city)) {
                    return array;
                }
            });
        } else if (data.price_min || data.price_max) {
            if (data.price_min && typeof data.price_min != "number") {
                let multiplication_price_min = data.price_min.slice(-1);

                if (multiplication_price_min === "k") {
                    data.price_min = parseInt(data.price_min.slice(1)) * 1000;
                } else if (multiplication_price_min === "M") {
                    data.price_min = parseInt(data.price_min.slice(1)) * 1000000;
                }
            }

            if (data.price_max && typeof data.price_max !== "number") {
                let multiplication_price_max = data.price_max.slice(-1);

                if (multiplication_price_max === "k") {
                    data.price_max = parseInt(data.price_max.slice(1)) * 1000;
                } else if (multiplication_price_max === "M") {
                    data.price_max = parseInt(data.price_max.slice(1)) * 1000000;
                }
            }

            // console.log(data.price_max);
            // console.log(data.price_min);

            if  (data.price_max || data.price_min){
                array = array.filter (obj => checkRange(data.price_min, data.price_max,obj, "price"));
            }
        }else if (data.bed_min || data.bed_max) {
                array = array.filter (obj => checkRange(data.bed_min, data.bed_max,obj, "number_of_beds"));
        }else if (data.room_min || data.room_max) {
            array = array.filter(obj => checkRange(data.room_min, data.room_max, obj, "number_of_rooms"));
        }

        this.setState({
            apartments: array
        });

    };

    render() {

        const {apartments} = this.state;

        const buildSquareDetails = () => {
            return apartments.map((item, i) => {
                return (
                    <Card {...item} key={i}/>
                )
            })
        };

        return (
            <div>
                <div>
                    <Filters updateGalleryItems={this.updateGalleryItems}/>
                </div>

                <div className={"row"}>
                    {buildSquareDetails()}
                </div>
            </div>
        )
    }
}


export default Gallery;
