import React from 'react';
import "../../css/singleApartmentCss/single_apartment.css"
import axios from "axios";
import GoogleMaps from "./google_maps";
import SingleApartmentData from "./single_apartment_data";

class SingleApartment extends React.Component{
    constructor(props){
        super(props);
        // const apartment = this.getApartmentDataId(this.props.aptId);
        // const city = this.getCityDataId(this.props.aptId);

        this.state = {
            apartment: null,
            city: null,
            img_apartment: null,
            loading:true,

        }
    };

    componentDidMount(){
        this.getdata();
    }
    async getdata(){
        const data = await axios.get(`http://localhost:5000/apartments/${this.props.aptId}`);
        this.setState({apartment:data.data[0],loading:false});
        this.setState({img_apartment:this.state.apartment.images.toString().split(',')});
        console.log(this.state.apartment);
    }

    // getApartmentDataId =  (id) => {
    //     return this.props.apartments.find(apartment => apartment.id === parseInt(id))
    // };
    //
    // getCityDataId =  (id) => {
    //     return this.props.cities.find(city => city.id === parseInt(id))
    // };

    // carousel_images = (array) => {
    //     this.state.apartment.images.map(img => {
    //         return(array.push(
    //              <div className="carousel-item images_carousel">
    //                 <img src={`../images/apartment/${img}`} className="d-block w-100" alt="..."/>
    //             </div>))
    //         }
    //     );return array;
    // };

    render() {

        const {apartment, img_apartment} = this.state;
        // const city_id = this.props.cities.find(city => city.id === apartment.cityId);
        let carousel_images = [];
        if(img_apartment) {
            carousel_images.push(img_apartment.map((item,i) =>  {
                return (
                    <div className='carousel-item' key={i}>
                        <img src={`http://localhost:5000/${item}`} className="d-block w-100" alt="/"/>
                    </div>
                )
            }))
        }


        return(

            <div className={"container"}>
                <div className={"inpo_above_picture"}>
                    <p>Presented by:<span> Andy H. Kim</span></p>
                    <p>Brokered by: <span>Nest Seekers International, Westside</span></p>
                </div>
                { apartment ?
                    <div>
                        <div className={"carousel"}>
                            <div id="carouselExampleControls" className={"carousel slide images_carousel"} data-ride="carousel">
                                <div className="carousel-inner">
                                    <div className={"carousel-item active images_carousel"}>
                                        <img src={`http://localhost:5000/${apartment.main_image}`} className="d-block w-100" alt="..."/>
                                    </div>
                                    {carousel_images}
                                </div>
                                <a className="carousel-control-prev" href="#carouselExampleControls" role="button"
                                   data-slide="prev">
                                    <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                                    <span className="sr-only">Previous</span>
                                </a>
                                <a className="carousel-control-next" href="#carouselExampleControls" role="button"
                                   data-slide="next">
                                    <span className="carousel-control-next-icon" aria-hidden="true"></span>
                                    <span className="sr-only">Next</span>
                                </a>
                                <h2 className={"img_absolute new_home"}>NEW</h2>
                                {(apartment.for_rent || apartment.for_sale) &&
                                <h2 className={"img_absolute status"}>{apartment.for_sale ? "for sale"  : apartment.for_rent && "for rent" }</h2>
                                }
                            </div>


                        </div>

                        <div className={"d-flex justify-content-between inpo_div"}>
                            <SingleApartmentData apartment={apartment}/>
                            <GoogleMaps apartment={apartment}/>
                        </div>
                    </div>
                    : null}
            </div>
        )
    }
}

export default SingleApartment;
