function create_box(){

    function buildSquareDetails(tagname, name, value, parent_div, label = "" ) {
        var element = document.createElement(tagname);
        apartment_div.setAttribute('data-' + name, value);

        if (tagname == "IMG"){
            element.setAttribute("src", value);
            element.classList.add("card-img-top");
            element.setAttribute("onmouseover", "hover_img(this," +i+ ")");
            element.setAttribute("onmouseout", "no_hover_img(this," +i+ ")");

        }else{
            var elemnt_text = document.createTextNode(label + value);
            element.appendChild(elemnt_text);
        }

        parent_div.appendChild(element);
    }

    function build_element_and_class(element_name, class_list, parent_div, tagname){
        var element_name = document.createElement(tagname);
        for (var i = 0; i < class_list.length; i++){
            element_name.classList.add(class_list[i]);
        }
        parent_div.appendChild(element_name);
        return element_name;
    }

    for (var i in apartments){

        var apartment = document.getElementById("box_row");

        var apartment_div = build_element_and_class(apartment_div, ["card","col-xl-3", "col-lg-4", "col-sm-6", "box"], apartment, "div" );
        var shadow_box = build_element_and_class(shadow_box, ["shadow_box"],apartment_div, "div");
        var price = build_element_and_class(price, ["price"], shadow_box, "div")

        buildSquareDetails("IMG", "image", apartments[i].main_image, price);
        buildSquareDetails("H3", "time", "NEW - 12 HOURS AGO" , price );
        buildSquareDetails("H4", "price", apartments[i].price , price, "$");

        var icon_heart = build_element_and_class(icon_heart, ["heart"], price, "div");
        icon_heart.innerHTML='<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 40 40" aria-labelledby="Save Listing" width="40" height="40" tabindex="-1" class="jsx-2877931502 "><path fill="rgba(0,0,0,0.4)" stroke="#fff" stroke-width="3" d="M20 8.3c4.9-8 18.5-5.9 18.5 5l-.1 1.9c-.8 4.6-4 9.3-8.9 14a66.6 66.6 0 0 1-8.7 7l-.7.6-.8-.5a27.6 27.6 0 0 1-2.8-1.7c-2-1.4-4-3-6-4.7-5.6-5-9-10.3-9-15.8A10 10 0 0 1 20 8.3z" class="jsx-2877931502"></path></svg>';
        var data_box = build_element_and_class(data_box, ["data_box"], shadow_box, "div");
        var data_ul = build_element_and_class(data_ul, ["d-flex"], data_box, "ul");

        buildSquareDetails("li", "bed_number", apartments[i].number_of_beds, data_ul, "bed: ");
        buildSquareDetails("li", "room_number", apartments[i].number_of_rooms, data_ul, "room: ");

        if (apartments[i].sqft != undefined){
            buildSquareDetails("li", "sqft_number", apartments[i].sqft, data_ul, "sqft: ")
        }

        buildSquareDetails("span", "address", apartments[i].address , data_box);

        var cityId = apartments[i].cityId;
        var city_name = cities.find( city => city.id == cityId);

        buildSquareDetails("span", "city", city_name.label, data_box , ", ");

        if(i >= 12){
            toggleElement(apartment_div, false);
        }
    }
