function getFormData() {
    var apartments_city = document.getElementById("apartments_citys").value;
    var number_of_beds = document.getElementById("number_of_beds").value;
    var number_of_rooms = document.getElementById("number_of_rooms").value;
    var price = getRadioValue("price");
    return {city: apartments_city, beds: number_of_beds, rooms: number_of_rooms ,price: price};
}

function getRadioValue(name) {
    var inputsRadio = document.getElementsByName(name);
    for (var i = 0; i < inputsRadio.length; i++) {
        if (inputsRadio[i].checked) {
            return inputsRadio[i].value;
        }
    }
}

function checkFilters(current, formData) {
    current = {
        city: current.getAttribute("data-city").toLowerCase(),
        beds: current.getAttribute("data-bed_number").toLowerCase(),
        rooms: current.getAttribute("data-room_number").toLowerCase(),
        price: current.getAttribute("data-price")
    };
    var price_min, price_max;
    if (formData.price) {
        price_min = parseInt(formData.price.slice(0, formData.price.indexOf('-')));
        price_max = parseInt(formData.price.slice(formData.price.indexOf('-') + 1));
    } else {
        price_min = 0;
        price_max = 10000;
    }

    var filter_city = current.city.includes(formData.city),
        filter_beds = current.beds.includes(formData.beds),
        filter_rooms = current.rooms.includes(formData.rooms),
        filter_price = price_max >= parseInt(current.price) && parseInt(current.price) > price_min;

    return filter_city && filter_beds && filter_rooms && filter_price;
}

function handleSubmit() {
    var div_show_more = document.getElementById("button_show_more");
    div_show_more.classList.add("hide");

    event.preventDefault();
    var formData = getFormData(),
        all_boxs = document.getElementsByClassName("box");

    for (var i = 0; i < all_boxs.length; i++) {
        var current = all_boxs[i];
        toggleElement(current, checkFilters(current, formData));
    }
}

function toggleElement(el, is_show) {
    if (is_show) {
        el.classList.add("show");
        el.classList.remove("hide");
    } else {
        el.classList.add("hide");
        el.classList.remove("show");
    }
}

function clearAll() {
    var all_boxs = document.getElementsByClassName("box");
    for (var i = 0; i < all_boxs.length; i++) {
        if (i < 12){
            toggleElement(all_boxs[i], true);
        }else{
            toggleElement(all_boxs[i], false);
        }
    }
    var div_show_more = document.getElementById("button_show_more");
    div_show_more.classList.add("show");
    div_show_more.classList.remove("hide");
}

function show_more(){
    var all_boxs = document.getElementsByClassName("box");
    var div_show_more = document.getElementById("button_show_more");

    for (var i = 0; i < all_boxs.length; i++) {
        if (i >= 12){
            toggleElement(all_boxs[i], true);
        }
    }
    div_show_more.classList.add("hide");
}


