import React from 'react';
import {cities} from "../components/cities_array";


class Card extends React.Component{
    render(){
    const {label, main_image, image, price, description, number_of_beds, number_of_rooms, sqft, country, address, cityId} = this.props;
        const heart = <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 40 40" aria-labelledby="Save Listing" width="40" height="40" tabIndex="-1" className="jsx-2877931502 ">
                    <path fill="rgba(0,0,0,0.4)" stroke="#fff" stroke-width="3" d="M20 8.3c4.9-8 18.5-5.9 18.5 5l-.1 1.9c-.8 4.6-4 9.3-8.9 14a66.6 66.6 0 0 1-8.7 7l-.7.6-.8-.5a27.6 27.6 0 0 1-2.8-1.7c-2-1.4-4-3-6-4.7-5.6-5-9-10.3-9-15.8A10 10 0 0 1 20 8.3z" className="jsx-2877931502"></path></svg>;

        const src = !main_image ? image : main_image;
        const num_beds_or_contry_name = number_of_beds ? "beds: " + number_of_beds : "country: " + country;
        const num_rooms_or_city_name = number_of_rooms ? "rooms: " + number_of_rooms : "city: " + label;
        const sqft_or_null = sqft ? "sqft: " + sqft : "";
        const city = cities.find(city => city.id === cityId);
        const city_or_null = city ? ", " + city.label : "";


        return(
            <div className={" col-xl-3 col-lg-4 col-sm-6 box"}>
                <div className={"card shadow_box"}>
                    <div className={"price"}>
                        <img className={"card-img-top"} src= {src} alt=""/>
                        <h3>NEW - 12 HOURS AGO</h3>
                        <h4>{price ? price : description}</h4>
                        <div className={"heart"}>{heart}</div>
                    </div>
                    <div className={"data_box"}>
                        <ul className={"d-flex"}>
                            <li>{num_beds_or_contry_name}</li>
                            <li>{num_rooms_or_city_name}</li>
                            <li>{sqft_or_null}</li>
                        </ul>
                        <span>{address}</span>
                        <span>{city_or_null}</span>
                    </div>
                </div>
            </div>
        )
    }
}

export default Card;


