import React from 'react';
import {Link} from "react-router-dom";
import '../../css/cardCss/card.css';


class Card extends React.Component{
    render(){
        const {label, main_image, image, price, description, number_of_bath, number_of_rooms, sqft, country, address, cityId, type} = this.props;
        const src = !main_image ? `images/cities/${image}` : `http://localhost:5000/${main_image}`;
        const price_or_description = price ? `$${price}`: description;
        const num_bath_or_contry_name = number_of_bath ? "beds: " + number_of_bath : "country: " + country;
        const num_rooms_or_city_name = number_of_rooms ? "rooms: " + number_of_rooms : "city: " + label;
        const sqft_or_null = sqft ? "sqft: " + sqft : "";
        const city =  type === "loading" ? "" : this.props.cities.find(city => city.id === cityId);
        const city_or_null = city ? ", " + city.label : "";

        const heart = <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 40 40" aria-labelledby="Save Listing" width="40" height="40" tabIndex="-1" className="jsx-2877931502 ">
            <path fill="rgba(0,0,0,0.4)" stroke="#fff" strokeWidth="3" d="M20 8.3c4.9-8 18.5-5.9 18.5 5l-.1 1.9c-.8 4.6-4 9.3-8.9 14a66.6 66.6 0 0 1-8.7 7l-.7.6-.8-.5a27.6 27.6 0 0 1-2.8-1.7c-2-1.4-4-3-6-4.7-5.6-5-9-10.3-9-15.8A10 10 0 0 1 20 8.3z" className="jsx-2877931502"></path></svg>;


        return(
            <div className={"col-xl-3 col-lg-4 col-sm-6"}>
                { type === "loading" ?
                    <div className={"box loading_box"}>
                        <div className={"card shadow_box"}>
                            <div className={"price"}>
                                <img className={"card-img-top"} src= {`./images${this.props.loading_img}`} alt="/"/>
                            </div>

                            <div className={"data_box"}>
                                <div className={"loading_background sort_loading_background"}><span className={"loading_sine"}></span></div>
                                <div className={"loading_background"}><span className={"loading_sine"}></span></div>
                            </div>
                        </div>
                    </div>

                :

                <div className={"box"}>
                    <div className={"card shadow_box"}>
                        <Link to={ type === "apartments" && `/singleApartment/${this.props.id}`}>
                            <div className={"price"}>
                                <img className={"card-img-top"} src= {src} alt="/"/>
                                <h3>${this.props.description}</h3>
                                <h4>{price_or_description}</h4>
                                <div className={"heart"}>{heart}</div>
                            </div>
                        </Link>
                        <div className={"data_box"}>
                            <ul className={"d-flex"}>
                                <li>{num_bath_or_contry_name}</li>
                                <li>{num_rooms_or_city_name}</li>
                                <li>{sqft_or_null}</li>
                            </ul>
                            <span>{address}</span>
                            <Link to={`/CitiesPage`}><span>{city_or_null}</span></Link>
                        </div>
                    </div>
                </div>
                }
            </div>

        )
    }
}

export default Card;
