import React from 'react';
import Card from "../search_page/card";


class CitiesPage extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            cities: this.props.cities,
            filtered: this.props.cities,
        }
    };

    updateGalleryItemss  = (data) => {
        const {filtered, cities} = this.state;
        console.log(data.search_city);

        if (data.search_country) {
            console.log(data.search_country);
            this.setState({
                filtered: filtered.filter(function (country) {
                    if (country.country.toLowerCase().includes(data.search_country.toLowerCase())) {
                        return filtered
                    }
                })
            })
        }
        if (data.search_city) {
            this.setState({
                filtered: filtered.filter(function (city) {
                    if (city.label.toLowerCase().includes(data.search_city.toLowerCase())) {
                        return filtered
                    }
                })
            })
        }
        if (data.no_filters){
            this.setState({
                filtered : cities,
            });
        }
    };

    handleSubmit = (e) => {
        e.preventDefault();
        const data = {
            search_country: this.state.search_country,
            search_city: this.state.search_city,
            no_filters: this.state.no_filters,
        };

        this.updateGalleryItemss(data);
    };

    handleChange = (e) => {
        const {name, value} = e.target;
        this.setState({
            [name] : value,
        });
        const data = {
            [name]:value
        };
        this.updateGalleryItemss(data)
    };



    render() {

        const {filtered} = this.state;
        const buildSquareDetails = () => {
            return filtered.map((item, i) => {
                return (
                    <Card {...item} cities={filtered} key={i}/>
                )
            })
        };

        return(
            <div style={{overflow:"hidden"}}>
                <h1 className={"titel_of_boxs"}>My Cities</h1>

                <div className={"d-flex justify-content-center m-10px"}>
                    <form className="d-flex" onSubmit={this.handleSubmit}>
                        <input type="text" name="search_country" onChange={this.handleChange}
                               placeholder="Search country"/>
                        <button className={"butt_filter_city"}><i className="fas fa-search"></i></button>

                        <input type="text" name="search_city" onChange={this.handleChange}
                               placeholder="Search city"/>
                        <button className={"butt_filter_city"}><i className="fas fa-search"></i></button>

                        <div className="d-none d-lg-flex" id="save_search">
                            <button name="no_filters" value={true} onClick={this.handleChange}>All countries</button>
                        </div>
                    </form>
                </div>

                <div className={"row m-0 d-flex justify-content-around"}>
                    {buildSquareDetails()}
                </div>
            </div>
        )
    }
}

export default CitiesPage;
