import React from 'react';
import './App.css';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";
import SearchPage from "./components/search_page/search_page";
import HomePage from "./components/home_page/home_page";
import SingleApartment from "./components/single_apartment_page/single_apartment";
import Header from "./components/header_top/header";
import {getDataFromServer} from "./components/server_data";
import {cities} from "./components/app_data/cities_array";


class App extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            apartments: [],
            loading: true,
        };
    };

    componentDidMount() {
        getDataFromServer('data-rt.json',this.handleSuccess);
    };

    handleSuccess = (data) => {
        this.setState({
            apartments: data.apartments,
            cities: data.cities,
            loading: false,
        })
    };
    render() {
        const {apartments, loading} = this.state;
        return(
            <div>

                <Router>
                    <Header/>
                    {loading ? <p>LOADING</p> :
                        <Switch>

                            <Route path="/SearchPage">
                                <SearchPage apartments={apartments} cities={cities}/>
                            </Route>

                            <Route path={"/singleApartment/:id"}
                                   component={(props) => <SingleApartment apartments={apartments}
                                                                          aptId={props.match.params.id}
                                                                          cities={cities}/>}/>

                            <Route path="/">
                                <HomePage apartments={apartments}/>
                            </Route>

                        </Switch>
                    }
                </Router>
            </div>
        )
    }
}

export default App;
