import React from 'react';
import Card from "./card.jsx";
import Filters from "./filters";
import {apartments} from "../app_data/apartments_array";
import {cities} from "../app_data/cities_array";

class Gallery extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            apartments: apartments,
        }
    }

    updateGalleryItems = (data) => {
        let array = [];

        if (data.city) {
            apartments.map(function(apartment){
                const city = cities.find(city => city.id === apartment.cityId);
                const city_name = city.label;
                if (city_name.includes(data.city)){
                    array.push(apartment);
                }
            });
        }
        else if(data.price_min || data.price_max){
            if (typeof data.price_min != "number" ){
                let multiplication_price = data.price_min.slice(-1);
                data.price_min = parseInt(data.price_min.slice(1));

                if (multiplication_price === "k"){
                    data.price_min = data.price_min*1000;
                }else if (multiplication_price === "m"){
                    data.price_min = data.price_min *10000;
                }
            }

            if (typeof data.price_max != "number" ){
                let multiplication_price = data.price_max.slice(-1);
                data.price_max = parseInt(data.price_max.slice(1));

                if (multiplication_price === "k"){
                    data.price_max = data.price_max*1000;
                }else if (multiplication_price === "m"){
                    data.price_max = data.price_max *10000;
                }
            }

            console.log(data.price_max);
            console.log(data.price_min);
            apartments.map(function(apartment){
                if ( data.price_min <= apartment.price <= data.price_max){
                    array.push(apartment);
                }
            });
        }


        this.setState({
            apartments : array,
        })
    };

    render() {

        const {apartments} = this.state;

        // const {items} = this.props;
        const buildSquareDetails = () => {
            return apartments.map((item, i) => {
                return (
                    <Card {...item} key = {i}/>
                )
            })
        };

        return(
            <div>
                <div>
                    <Filters updateGalleryItems = {this.updateGalleryItems} />
                </div>

                <div className={"row"}>
                    {buildSquareDetails()}
                </div>
            </div>
        )

    }

}

export default Gallery;
