import React from 'react';
import './filters.css';

class Filters extends React.Component{

    handleSubmit = () => {
        // e.preventDefault();
        const data = {
            city: this.state.city,
            price_min: this.state.price_min,
            price_max: this.state.price_max,
            // rooms: this.state.rooms,
        };
        this.props.updateGalleryItems(data);
    };

    handleChange = (e) => {
        this.handleSubmit();

        const {name, value} = e.target;
        this.setState({
            [name] : value,
        });


    };

    render() {
        return(
            <div className="container-fluid">
                <div className="row d-flex" id="second_row">
                    <div id="search_button">

                        <form className="d-flex" >
                            <input type="text" name="city" onChange={this.handleChange}
                                   placeholder="Address, city, Neighborhood or Zip"/>
                                   <button className={"butt_filter_city"}><i className="fas fa-search"></i></button>


                            <div className="row d-none d-lg-flex" id="dropdown_buttons">
                                <div className="dropdown dropdown_button">
                                    <button className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton"
                                            data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                        price
                                    </button>
                                    <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                                        <div className="dropdown_titel"><p>Price range</p></div>

                                        <div className="row d-flex flex-column">

                                            <div className="d-flex justify-content-around input_data_list">
                                                 <input list="select_baths_from" type="text" name="price_min" onChange={this.handleChange} placeholder="NO MIN"/>
                                                        <datalist id="select_baths_from">
                                                            <option value="$0"/>
                                                            <option value="$250k"/>
                                                            <option value="$450k"/>
                                                            <option value="$700k"/>
                                                            <option value="$900k"/>
                                                            <option value="$1.2M"/>
                                                            <option value="$1.4M"/>
                                                            <option value="$1.6M"/>
                                                        </datalist>

                                                        <span> - </span>

                                                        <input list="select_baths_to" type="text" name="price_max" onChange={this.handleChange} placeholder="NO MAX"/>
                                                            <datalist id="select_baths_to">
                                                                <option value="$400k"/>
                                                                <option value="$800k"/>
                                                                <option value="$1.2M"/>
                                                                <option value="$1.6M"/>
                                                                <option value="$2M"/>
                                                                <option value="$2.4M"/>
                                                                <option value="$2.8M"/>
                                                                <option value="Any Price"/>
                                                            </datalist>
                                                </div>


                                        </div>
                                    </div>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        )
    }
}

export default Filters;
