import React from 'react';
import {navigation} from "../app_data/header_array";
import Inner_nav_item from "./inner_nav_item.jsx";

class Main_nav_item extends React.Component{
    constructor(){
        super();
        this.state = {
            active_menu: null,
        }
    }

    change_active_menu = (index) => {
        this.setState({
            active_menu: this.state.active_menu === index ? -1 : index,
        })
    }

    render() {
        return(
            <ul className={"inner_nav_ul"}>
                {navigation.map((main_nav_item, i) => {
                   return(
                      <li className={"inner_nav_li"} key={i}>
                            <h4 onMouseOver={() => this.change_active_menu(i)}
                                onMouseOut={() => this.change_active_menu(-1)}
                                className={"inner_nav_li_label"}>{main_nav_item.label}</h4>
                            {this.state.active_menu === i && <Inner_nav_item inner_menu={main_nav_item.inner_menu}/>}
                      </li>
                   )
                })}
            </ul>
        )
    }
}

export default Main_nav_item;
