import React from 'react';
import Card from "../search_page/card";


class CitiesPage extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            cities: this.props.cities,
            filtered: this.props.cities,
        }
    };

    updateGalleryItemss  = (data) => {
        const {filtered, cities} = this.state;
        console.log(data.search_city);


        this.setState({
            filtered: filtered.filter(function (country) {
                if (country.country.toLowerCase().includes(data.search.toLowerCase()) || country.label.toLowerCase().includes(data.search.toLowerCase()))
                {
                    return filtered
                    }
                })
            });

        if (data.no_filters){
            this.setState({
                filtered : cities,
            });
        }
    };

    handleSubmit = (e) => {
        e.preventDefault();
        const data = {
            search_country: this.state.search_country,
            search_city: this.state.search_city,
            no_filters: this.state.no_filters,
        };

        this.updateGalleryItemss(data);
    };

    handleChange = (e) => {
        const {name, value} = e.target;
        this.setState({
            [name] : value,
        });
        const data = {
            [name]:value
        };
        this.updateGalleryItemss(data)
    };



    render() {

        const {filtered} = this.state;
        const buildSquareDetails = () => {
            return filtered.map((item, i) => {
                return (
                    <Card {...item} cities={filtered} key={i}/>
                )
            })
        };

        return(
            <div style={{overflow:"hidden"}}>

                <div className={"d-flex justify-content-center"} style={{ padding :"20px 0 20px 0"}}>
                    <form className="d-flex" onSubmit={this.handleSubmit}>
                        <input type="text" name="search" onChange={this.handleChange}
                               placeholder="Search"/>
                        <button className={"butt_filter_city"}><i className="fas fa-search"></i></button>

                        <div className={"d-none d-lg-flex no_filters"}>
                            <button name="no_filters" value={true} onClick={this.handleChange}>All countries</button>
                        </div>
                    </form>
                </div>

                <div className={"row m-0 d-flex justify-content-around"}>
                    {buildSquareDetails()}
                </div>
            </div>
        )
    }
}

export default CitiesPage;
