import React from 'react';
import "../../css/login_signupCss/login_signupCSS.css"
import validate, {field} from '../../components/login_signup/validator';
import InputErrors from "../../components/login_signup/inputErrors";
import api from "../../server_api/api";

class MySpace extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            address: field({name: 'address', isRequired: true}),
            city: field({name: 'city', isRequired: true}),
            price: field({name: 'price', isRequired: true}),
            number_of_room:field({name: 'number_of_room', isRequired: true}),
            number_of_bath: field({name: 'number_of_bath', isRequired: true}),
            sqft: field({name: 'sqft', isRequired: true}),
            description: field({name: 'description', isRequired: true}),
            sale_status: field({name: 'sale_status', isRequired: true}),
            property_type: field({name: 'property_type', isRequired: true}),
            main_image: field({name: 'main_image', isRequired: true}),
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange({target:{name,value}}){
        const errors = validate(name, value, this.state[name].validations);

        this.setState({
            [name]: {
                ...this.state[name],
                value,
                errors
            }
        });
    }

    handleSubmit = (event)=> {
        event.preventDefault();
        let isOK = true;
        for (let prop in this.state) {
            const fa = this.state[prop];
            const errors = validate(prop, fa.value, fa.validations);
            if (errors.length) {
                isOK = false;
                this.setState({
                    [prop]: {
                        ...this.state[prop],
                        errors
                    }
                });
            }
        }
        if(isOK){
            const result = this.state;
            for(let prop in this.state){
                result[prop] = this.state[prop].value;
            }
            api.loginUser(result)
                .then(res => {console.log(res)})

        }
    };



    render() {
        return (
            <div className={"row"}>
                <div className="col-md-5 mx-auto">
                    <div className="myform form ">
                        <div className="logo mb-3">
                            <div className="col-md-12 text-center">
                                <h1 className={"head_line"}>Upload new apartment</h1>
                            </div>
                        </div>
                        <form onSubmit={this.handleSubmit}>
                            <div className="form-row">

                                <div className="col-md-4 mb-3">
                                    <label htmlFor="validationDefault01">Address</label>
                                    <input type="text" className="form-control" name="address" placeholder="Enter address" onBlur={this.handleChange} />
                                </div>
                                <div className={"error"}><InputErrors errors={this.state.address.errors}></InputErrors></div>

                                <div className="col-md-4 mb-3">
                                    <label htmlFor="validationDefault01">City</label>
                                    <input type="text" className="form-control" name="city" placeholder="Enter city" onBlur={this.handleChange} />
                                </div>
                                <div className={"error"}><InputErrors errors={this.state.city.errors}></InputErrors></div>

                                <div className="col-md-4 mb-3">
                                    <label htmlFor="validationDefault01">Price</label>
                                    <input type="number" className="form-control" name="price" placeholder="Enter price" onBlur={this.handleChange} />
                                </div>
                                <div className={"error"}><InputErrors errors={this.state.price.errors}></InputErrors></div>

                                <div className="col-md-4 mb-3">
                                    <label htmlFor="validationDefault01">Number of room</label>
                                    <input type="number" className="form-control" name="number_of_room" placeholder="Enter number of room" onBlur={this.handleChange} />
                                </div>
                                <div className={"error"}><InputErrors errors={this.state.number_of_room.errors}></InputErrors></div>

                                <div className="col-md-4 mb-3">
                                    <label htmlFor="validationDefault01">Number of bath</label>
                                    <input type="number" className="form-control" name="number_of_bath" placeholder="Enter number of bath" onBlur={this.handleChange} />
                                </div>
                                <div className={"error"}><InputErrors errors={this.state.number_of_bath.errors}></InputErrors></div>

                                <div className="col-md-4 mb-3">
                                    <label htmlFor="validationDefault01">SQFT</label>
                                    <input type="number" className="form-control" name="sqft" placeholder="Enter sqft" onBlur={this.handleChange} />
                                </div>
                                <div className={"error"}><InputErrors errors={this.state.sqft.errors}></InputErrors></div>

                                <div className="col-md-4 mb-3">
                                    <label htmlFor="validationDefault01">Description</label>
                                    <input type="text" className="form-control" name="description" placeholder="Enter description" onBlur={this.handleChange} />
                                </div>
                                <div className={"error"}><InputErrors errors={this.state.description.errors}></InputErrors></div>

                                {/*<div>*/}
                                {/*    <label htmlFor={"rent_radio"}>Rent </label><input type="radio" id={"rent_radio"} name="sale_status" value="rent" onClick={this.handleChange}/>*/}
                                {/*    <label htmlFor={"sale_radio"}>Sale </label><input type="radio" id={"sale_radio"} name="sale_status" value="sale" onClick={this.handleChange}/>*/}
                                {/*    <label htmlFor={"both_radio"}>Both </label><input type="radio" id={"both_radio"} name="sale_status" value="both" onClick={this.handleChange}/>*/}
                                {/*</div>*/}

                                <input list="city_list" type="text" name="city" onBlur={this.handleChange} placeholder="Enter city name"/>
                                <select id="city_list">
                                    <option value={"rent"}/>
                                    <option value={"sale"}/>
                                    <option value={"both"}/>
                                </select>


                                {/*<div className="form-group d-flex input_div">*/}
                                {/*    <label className={"input_label"} htmlFor="exampleInputEmail1">First Name</label>*/}
                                {/*    <input type="text" name="first_name"*/}
                                {/*           className="form_input" id="firstname" aria-describedby="emailHelp"*/}
                                {/*           placeholder="Enter first name" onBlur={this.handleChange}/>*/}
                                {/*</div>*/}
                                {/*<div className={"error"}><InputErrors*/}
                                {/*    errors={this.state.first_name.errors}></InputErrors></div>*/}
                            </div>
                            <button className="btn btn-primary" type="submit">Submit form</button>
                        </form>
                    </div>
                </div>
            </div>

        )
    }
}

export default MySpace;
