import React from 'react';
import {apartments} from "../app_data/apartments_array";

class SingleApartment extends React.Component{
    constructor(props){
        super(props);
        const apartment_id = this.props.match.params.id;
        this.state = {
            apartment: this.getApartmentDataId(apartment_id),
        }
    };

    getApartmentDataId =  (id) => {
        return apartments.find(apartment => apartment.id === parseInt(id))
    };

    carousel_images = () => {
        this.props.apartment.images.map(img => {
            return(
                 <div className="carousel-item">
                <img src={`../${this.props.apartment.images[img]}`} className="d-block w-100" alt="..."/>
            </div>
        )});
    };

    render() {
        const {apartment} = this.state;

        return(

            <div>
                <div>
                    <p>Presented by: Andy H. Kim</p>
                    <p>Brokered by: Nest Seekers International, Westside</p>
                </div>

                <div className={"d-flex"}>
                    <div id="carouselExampleControls" className="carousel slide images_carousel " data-ride="carousel">
                        <div className="carousel-inner">
                            <div className="carousel-item active">
                                <img src={`../${apartment.main_image}`} className="d-block w-100" alt="..."/>
                            </div>
                            {this.carousel_images}
                        </div>
                        <a className="carousel-control-prev" href="#carouselExampleControls" role="button"
                           data-slide="prev">
                            <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                            <span className="sr-only">Previous</span>
                        </a>
                        <a className="carousel-control-next" href="#carouselExampleControls" role="button"
                           data-slide="next">
                            <span className="carousel-control-next-icon" aria-hidden="true"></span>
                            <span className="sr-only">Next</span>
                        </a>
                    </div>


                    <div>
                        <form>
                            <div className={"field"}>
                                <label>Title</label>
                                <input type={"text"}
                                       name={"title"}
                                       value=""
                                       onChange={this.handleChange}/>
                            </div>
                            <div className={"field"}>
                                <label>Your Post</label>
                                <textarea name={"body"}
                                          value=""
                                          onChange={this.handleChange}>

                            </textarea>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        )
    }
}

export default SingleApartment;
