import React from 'react';
import "../../css/singleApartmentCss/single_apartment.css"
import axios from "axios";

class SingleApartment extends React.Component{
    constructor(props){
        super(props);
        const apartment = this.getApartmentDataId(this.props.aptId);
        const city = this.getCityDataId(this.props.aptId);

        this.state = {
            apartment: apartment,
            city: city,
            img_apartment: [],
        }
    };

    componentDidMount(){
        this.getdata();
    }
    async getdata(){
        const data = await axios.get(`http://localhost:5000/apartments/${this.props.aprId}`);
        this.setState({apartment:data.data[0],loading:false});
        this.setState({images:this.state.apartment.images.toString().split(',')})
    }

    // getApartmentDataId =  (id) => {
    //     return this.props.apartments.find(apartment => apartment.id === parseInt(id))
    // };
    //
    // getCityDataId =  (id) => {
    //     return this.props.cities.find(city => city.id === parseInt(id))
    // };

    carousel_images = (array) => {
        this.state.apartment.images.map(img => {
            return(array.push(
                 <div className="carousel-item images_carousel">
                    <img src={`../images/apartment/${img}`} className="d-block w-100" alt="..."/>
                </div>))
            }
        );return array;
    };

    render() {
        const {apartment} = this.state;
        const city_id = this.props.cities.find(city => city.id === apartment.cityId);

        return(
            <div className={"container"}>
                <div className={"inpo_above_picture"}>
                    <p>Presented by:<span> Andy H. Kim</span></p>
                    <p>Brokered by: <span>Nest Seekers International, Westside</span></p>
                </div>

                <div className={"carousel"}>
                    <div id="carouselExampleControls" className={"carousel slide images_carousel"} data-ride="carousel">
                        <div className="carousel-inner">
                            <div className={"carousel-item active images_carousel"}>
                                <img src={`../images/apartment/${apartment.main_image}`} className="d-block w-100" alt="..."/>
                            </div>
                            {this.carousel_images(this.state.img_apartment)}
                        </div>
                        <a className="carousel-control-prev" href="#carouselExampleControls" role="button"
                           data-slide="prev">
                            <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                            <span className="sr-only">Previous</span>
                        </a>
                        <a className="carousel-control-next" href="#carouselExampleControls" role="button"
                           data-slide="next">
                            <span className="carousel-control-next-icon" aria-hidden="true"></span>
                            <span className="sr-only">Next</span>
                        </a>
                        <h2 className={"img_absolute new_home"}>NEW</h2>
                        {(apartment.for_rent || apartment.for_sale) &&
                        <h2 className={"img_absolute status"}>{apartment.for_sale ? "for sale"  : apartment.for_rent && "for rent" }</h2>
                        }
                    </div>


                    <div className={"d-flex more_inpo_form "}>
                        <h3>More about this property</h3>
                        <div className={"icons_form"}><i className={"fas fa-user"}></i>
                            <i className={"fas fa-envelope"}></i>
                            <i className={"fas fa-phone-alt"}></i>
                        </div>
                        <form>
                            <div className={"field"}>
                                <input type={"text"}
                                       placeholder={"Your Name"}
                                       name={"name"}
                                       onChange={this.handleChange}/>
                            </div>
                            <div className={"field"}>
                                <input type={"email"}
                                       placeholder={"Email"}
                                       name={"email"}
                                       onChange={this.handleChange}/>
                            </div>
                            <div className={"field"}>
                                <input type={"tel"}
                                       pattern={"[0-9]{3}-[0-9]{2}-[0-9]{3}"}
                                       placeholder={"Phone"}
                                       name={"phone"}
                                       onChange={this.handleChange}/>
                            </div>
                            <div className={"field"}>
                                <textarea name={"address"}
                                          placeholder={"Address"}
                                          onChange={this.handleChange}
                                            style={{paddingLeft:'10px'}}>

                                </textarea>
                            </div>
                            <button>Email Agent</button>
                            <p>By proceeding, you consent to receive calls and texts at the number you provided,
                                including marketing by autodialer and prerecorded and artificial voice, and email,
                                from realtor.com and others about your inquiry and other home-related matters,
                                but not as a condition of any purchase. More...</p>
                        </form>
                    </div>
                </div>

                <div className={"d-flex justify-content-between inpo_div"}>
                    <div className={"more_inpo"}>
                        <p className={"price"}>$ {apartment.price}</p>
                        <p className={""}><b>{apartment.number_of_beds}</b> beds <b>{apartment.number_of_rooms}</b> rooms
                            {apartment.sqft ?<span> <b>{apartment.sqft}</b> <span> sqft</span></span> : "" }</p>
                        <p className={""}>{apartment.address} , <span>{city_id.label}</span> </p>
                    </div>

                    <div className={"map_apartment"}>
                        <iframe src={`https://maps.google.com/maps?q=${(city_id.country+city_id.label).replace(/\s/g, '')}&t=&z=13&ie=UTF8&iwloc=&output=embed`}
                                 frameBorder="0"
                                 style={{border:"0"}} allowFullScreen>
                        </iframe>
                    </div>
                </div>
            </div>
        )
    }
}

export default SingleApartment;
