import React from 'react';
import './App.css';
import {
    BrowserRouter as Router,
    Switch,
    Route,

} from "react-router-dom";
import SearchPage from "./components/search_page/search_page";
import HomePage from "./components/home_page/home_page";
import SingleApartment from "./components/single_apartment_page/single_apartment";
import Header from "./components/header_top/header";
import {getDataFromServer} from "./components/app_data/server_data";
import CitiesPage from "./components/cities_page/cities_page";
import Loading from "./components/loading/loading";


class App extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            apartments: [],
            cities: [],
            loading: true,
        };
    };

    componentDidMount() {
        getDataFromServer('data-rt.json',this.handleSuccess);
    };

    handleSuccess = (data) => {
        setTimeout( () => {
            this.setState({loading: false,});

        },2000);

        this.setState({
            apartments: data.apartments,
            cities: data.cities,
        })
    };
    render() {
        const {apartments, cities, loading} = this.state;
        return(
            <div>

                <Router>
                    <Header/>

                        <Switch>

                            <Route path="/searchPage">
                                {loading ? <Loading type={"loading"}/> :<SearchPage apartments={apartments} cities={cities} type={"apartments"}/>}
                            </Route>

                            <Route path={"/citiesPage"}>
                                {loading ? <Loading type={"loading"}/> :<CitiesPage cities={cities} type={"cities"}/>}
                            </Route>

                            {loading ? <Loading type={"loading"}/> :<Route path={"/singleApartment/:id"}
                                   component={(props) => <SingleApartment apartments={apartments}
                                                                          aptId={props.match.params.id}
                                                                          cities={cities}/>}/>}

                            <Route path="/">
                                <HomePage apartments={apartments} cities={cities} type={"apartments"}/>
                            </Route>

                        </Switch>

                </Router>
            </div>
        )
    }
}

export default App;
