import React from 'react';
import Card from "../search_page/card";


class CitiesPage extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            filtered: this.props.cities,
        }
    };

    handleSubmit = (e) => {
        e.preventDefault();
        const data = {
            search_country: this.state.search_country,
            search_city: this.state.search_city,

        };
        this.updateGalleryItems(data);
    };

    handleChange = (e) => {
        const {name, value} = e.target;
        this.setState({
            [name] : value,
        });
    };

    updateGalleryItems  = (data) => {
        const {filtered, cities, search_country, search_city} = this.state;

        if (data.search_city) {
            console.log(data.search_city);
            this.setState({
                filtered: filtered.filter(function (city) {
                    if (city.label.toLowerCase().includes(data.search_city.toLowerCase())) {
                        return filtered
                    }
                })
            })
        }
    };

    render() {

        const {cities , filtered} = this.state;
        const buildSquareDetails = () => {
            return filtered.map((item, i) => {
                return (
                    <Card {...item} cities={filtered} key={i}/>
                )
            })
        };

        return(
            <div style={{overflow:"hidden"}}>
                <h1 className={"titel_of_boxs"}>My Cities</h1>

                <div>
                    <form className="d-flex" onSubmit={this.handleSubmit}>
                        <input type="text" name="search_country" onChange={this.handleChange}
                               placeholder="Search country"/>
                        <button className={"butt_filter_city"}><i className="fas fa-search"></i></button>

                        <input type="text" name="search_city" onChange={this.handleChange}
                               placeholder="Search city"/>
                        <button className={"butt_filter_city"}><i className="fas fa-search"></i></button>
                    </form>
                </div>

                <div className={"row m-0 d-flex justify-content-around"}>
                    {buildSquareDetails()}
                </div>
            </div>
        )
    }
}

export default CitiesPage;
